import React, { useState } from 'react';
import { BrowserRouter as Router, Link, Route, Switch, useHistory, useLocation } from 'react-router-dom';
import ChatRoom from './components/ChatRoom';
import UserPage from './pages/UserPage';

const Navigation = () => {
    const location = useLocation();
    const history = useHistory();

    const navigateTo = (path) => {
        history.push(path);
    };

    return (
        <nav>
            <ul>
                <li>
                    <button onClick={() => navigateTo('/')}>Chat Room</button>
                </li>
                <li>
                    <button onClick={() => navigateTo('/users')}>Users</button>
                </li>
            </ul>
        </nav>
    );
};

const App = () => {
    return (
        <Router>
            <div>
                <Navigation />
                <Switch>
                    <Route path="/" exact component={ChatRoom} />
                    <Route path="/users" component={UserPage} />
                </Switch>
            </div>
        </Router>
    );
};

export default App;
