import React, { useState, useEffect } from 'react';
import axios from 'axios';
import io from 'socket.io-client';

interface Message {
    id: number;
    text: string;
}

const ChatRoom: React.FC = () => {
    const [messages, setMessages] = useState<Message[]>([]);
    const [newMessage, setNewMessage] = useState<string>('');
    const socket = io('http://localhost:3001');

    useEffect(() => {
        // Отримання початкових повідомлень при підключенні
        axios.get('/api/messages')
            .then(response => setMessages(response.data))
            .catch(error => console.error('Error fetching initial messages:', error));

        // Підписка на нові повідомлення через socket.io
        socket.on('newMessage', (newMessage) => {
            setMessages((prevMessages) => [...prevMessages, newMessage]);
        });

        // Відключення при знищенні компонента
        return () => {
            socket.disconnect();
        };
    }, [socket]);

    const handleSendMessage = () => {
        axios.post('/api/messages', { text: newMessage })
            .then(response => {
                console.log('Message sent successfully:', response.data);
                setMessages((prevMessages) => [...prevMessages, response.data]);
                setNewMessage('');
            })
            .catch(error => console.error('Error sending message:', error));
    };

    return (
        <div>
            <h2>Chat Room</h2>
            <ul>
                {messages.map(message => (
                    <li key={message.id}>{message.text}</li>
                ))}
            </ul>
            <div>
                <input
                    type="text"
                    placeholder="Type your message..."
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                />
                <button onClick={handleSendMessage}>Send</button>
            </div>
        </div>
    );
};

export default ChatRoom;
