import React, { useState } from 'react';

interface LoginFormProps {
    onLogin?: (userData: { token: string, username: string }) => void;
}
const LoginForm: React.FC<LoginFormProps> = ({ onLogin }) => {
    const [userData, setUserData] = useState({
        username: '',
        password: ''
    });

    const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = event.target;
        setUserData({ ...userData, [name]: value });
    };

    const handleLogin = async () => {
        try {
            // Отправка запроса на сервер для аутентификации пользователя
            const response = await fetch('http://localhost:3001/api/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(userData)
            });

            // Обработка ответа от сервера
            if (response.ok) {
                const data = await response.json();
                const authToken = data.authToken;
                // Вызов функции обратного вызова onLogin с данными о пользователе
                if (onLogin) {
                    onLogin({ token: authToken, username: userData.username });
                }
            } else {
                // Обработка ошибок аутентификации
                console.error('Authentication error:', response.status);
            }
        } catch (error) {
            console.error('Authentication error:', (error as Error).message);
        }
    };

    return (
        <div>
            <label>
                username:
                <input
                    type="text"
                    name="username"
                    value={userData.username}
                    onChange={handleInputChange}
                />
            </label>
            <label>
                Password:
                <input
                    type="password"
                    name="password"
                    value={userData.password}
                    onChange={handleInputChange}
                />
            </label>
            <button onClick={handleLogin}>Login</button>
        </div>
    );
};

export default LoginForm;
