import React, { useState } from 'react';
import Modal from 'react-modal';

interface WarningModalProps {
    isOpen: boolean;
    onClose: () => void;
}

const WarningModal: React.FC<WarningModalProps> = ({ isOpen, onClose }) => {
    return (
        <Modal
            isOpen={isOpen}
            onRequestClose={onClose}
            contentLabel="OOOps,you are not authorized!"
        >
            <h2>Warning!</h2>
            <p>This is a warning message.</p>
            <button onClick={onClose}>Close</button>
        </Modal>
    );
};

const App = () => {
    const [isWarningOpen, setIsWarningOpen] = useState(false);

    const openWarningModal = () => setIsWarningOpen(true);
    const closeWarningModal = () => setIsWarningOpen(false);

    return (
        <div>
            <button onClick={openWarningModal}>Open Warning Modal</button>
            <WarningModal isOpen={isWarningOpen} onClose={closeWarningModal} />
        </div>
    );
};

export default App;
